                buildName=entry.getValue();
                userDataMap= NewPortalUtils.getNewLinkedHashMapWithKeyValueType();
                redirectURL= new URL(url+"/getSyncUserTicketsCombo.jsp?userNo="+userNo+"&buildName="+buildName);
