    			query.add("DELETE FROM FIN_SALES_REPORT WHERE SALES_REPORT_ID IN ("+salesReportID+")");
    			query.add("DELETE FROM FIN_SALES_REPORT_DETAILS WHERE SALES_REPORT_ID IN ("+salesReportID+")");
