    			query.add("DELETE FROM FIN_PL_REPORT_EXPENSE WHERE PLREP_ID IN ("+salesReportID+")");
    			query.add("DELETE FROM FIN_PL_REPORT_OVERHEAD WHERE PLREP_ID IN ("+salesReportID+")");
