    public static String handleSpecialForRegex(String s)
    {
    	try
    	{
    		if(StringUtil.isValid(s))
    		{
    			s = s.replaceAll(Pattern.quote("("), "\\\\(");
    			s = s.replaceAll(Pattern.quote(")"), "\\\\)");
    			s = s.replaceAll(Pattern.quote("."), "\\\\.");
    			s = s.replaceAll(Pattern.quote("?"), "\\\\?");
    			s = s.replaceAll(Pattern.quote("+"), "\\\\+");
    			s = s.replaceAll(Pattern.quote("*"), "\\\\*");
    			s = PortalUtils.replaceAll(s, "$", "\\$");
    			s = s.replaceAll(Pattern.quote("^"), "\\\\^");
    			s = s.replaceAll(Pattern.quote("["), "\\\\[");
    			s = s.replaceAll(Pattern.quote("^"), "\\^");
    			s = s.replaceAll(Pattern.quote("{"), "\\\\{");
    			s = s.replaceAll(Pattern.quote("|"), "\\\\|");
    		}

    	}
    	catch (Exception e) {
			// TODO: handle exception
    		e.printStackTrace();
		}
    	return s;
    }
